import React, { useState , useEffect } from 'react';
// import Products from './components/Products';
// import Box from './components/Box/Box';
// import Home from './components/Main/NavBar'
// import Product from './components/Product';
import dataFake from './dataFake';
import Routers from './routers'
import Product from './pages/views/Main/Product';
import apiRequest from "./api/productApi";

// import {
//   BrowserRouter as Router,
//   Switch,
//   Route,
//   Link
// } from "react-router-dom";
// import AddProduct from './components/AddProduct';
function App() {
  const [products, setProducts] = useState([]);
  const [status, setStatus] = useState(false);
  const [color, setColor] = useState('green');
  const onHandleClick = () => {
    // setStatus(true);
    setColor('red');
  };
  //Danh sách sản phẩm
  usrEffect(()=>{
    const getProducts = async () =>{
      try {
        const {data} = await apiRequest.getAll();
        setProducts(data);
      } catch (error) {
        console.log("failed to request API: ", error);
      }
    };
    getProducts();
  }, []);
  //Xóa sản phẩm
  const onHandleRemove = async (id) => {
    const newProducts = products.filter(product => product.id !== id);
    setProducts(newProducts);
  }
  const onHanleAdd = (product) => {
    const newProduct = {
      id: products.length + 1,
      ...product
    }
    setProducts([
      ...products,
      newProduct
    ])
  }
  const onHandleUpdate = (id, product) => {
    const abc = {...product};
    const newProducts = products.map(pro =>(pro.id == id ?{...product} : pro));
    setProducts(newProducts);
  }

  return (
    <div className="App">
      <Routers products={products} onRemove={onHandleRemove}  onAdd={onHanleAdd} onUpdate={onHandleUpdate} />

    </div>
  )

}
export default App;
