import React, { useState } from 'react'
import PropTypes from 'prop-types'

const AddProducts = ({onAdd}) => {
    const [valueInput, setValueInput] = useState({});
    const onHandleChange = (e) =>{
        const {name, value} =e.target
        setValueInput({
            ...valueInput,
            [name]:value
        });
    }
    const onHandleSubmit = (e)=>{
        e.preventDefault();
        onAdd(valueInput);
    }
    return (
        <div>
            <h1 className="h3 mb-2 text-gray-800">Add Product Form</h1>
            <div className="card shadow mb-4">
                <div className="card-body">
                    <form action="" onSubmit={onHandleSubmit}>
                        <div className="form-group">
                            <label htmlFor="">Tên sản phẩm</label>
                            <input type="text" name="name" className="form-control" onChange={onHandleChange}/> 
                        </div>
                        <div className="form-group">
                            <label htmlFor="">Ảnh</label>
                            <input type="text" name="image" className="form-control" onChange={onHandleChange}/> 
                        </div>
                        <div className="form-group">
                            <label htmlFor="">Giá</label>
                            
                            <input type="text" name="price" className="form-control" onChange={onHandleChange}/> 
                        </div>
                        <button type="submit" className="btn btn-primary">Submit</button>
                    </form>
                </div>
            </div>
        </div>
    )
}

AddProducts.propTypes = {

}

export default AddProducts
