import React, { useState } from 'react'
import PropTypes from 'prop-types'
import {useHistory} from 'react-router-dom'
import { useForm } from "react-hook-form";
import firebase from "../../../firebase";

const AddProducts = ({onAdd, categories}) => {
    const [valueInput, setValueInput] = useState({});
    const history = useHistory();
    const { register, handleSubmit , errors } = useForm();
    const onHandSubmit = (data) => {
        //upimage
        // console.log(data.image[0]);
        let file = data.image[0];
        // tạo reference chứa ảnh trên firesbase
        let storageRef = firebase.storage().ref(`images/${file.name}`);
        // đẩy ảnh lên đường dẫn trên
        storageRef.put(file).then(function () {
            storageRef.getDownloadURL().then((url) => {
                console.log(url);
                // Tạo object mới chứa toàn bộ thông tin từ input
                const newData = {
                    id: Math.random().toString(36).substr(2, 9),
                    ...data,
                    image: url,
                };
                console.log(newData);
                // đẩy dữ liệu ra ngoài app.js thông qua props onAdd
                onAdd(newData);
                history.push("/admin/products");
            });
        });
    };
    
    // const onSubmit = data => console.log(data);
 
    return (
        <div>
            <h1 className="h3 mb-2 text-gray-800">Add Product Form</h1>
            <div className="card shadow mb-4">
                <div className="card-body">
                    <form action="" onSubmit={handleSubmit(onHandleSubmit)}>
                        <div className="form-group">
                            <label htmlFor="">Tên sản phẩm</label>
                            <input type="text"
                            ref={register({
                                required: true,
                                minLength:2,
                                maxLength:20
                                
                              })} name="name" className="form-control" onChange={onHandleChange} /> 
                              {errors.name && errors.name.type ==='required'&&<span className='text-danger'>Bạn ko để trống </span>}
                              {errors.name && errors.name.type ==='minLength'&&<span  className='text-danger'>Không được để khoảng trăng </span>}
                              {errors.name && errors.name.type ==='maxLength'&&<span  className='text-danger'>Tối đa 5 kí tự</span>}
                        </div>
                        
                        <div className="form-group">
                        <label htmlFor="productPrice">Ảnh sản phẩm</label>
                        <div className="input-group">
                        <div className="custom-file">
                            <input type="file"
                                className="custom-file-input"
                                id="inputGroupFile02"
                                name="image"
                                ref={register}
                            />
                            <label className="custom-file-label" htmlFor="inputGroupFile02" aria-describedby="imageHelp">Choose image</label>
                        </div>
                        </div>
                        </div>
                      
                        <div className="form-group">
                            <label htmlFor="">Giá</label>
                            
                            <input type="text"
                            ref={register({
                                required: true,
                                
                                
                              })}  name="price" className="form-control"  onChange={onHandleChange} /> 
                              {errors.price && errors.price.type ==='required'&&<span  className='text-danger'>Bạn ko để trống </span>}
                        </div>
                        <label htmlFor="" > Danh mục</label>
                        <select className="form-control" name="id_cate" ref={register} onChange={onHandleChange}>
                      
                        <option value={0}>Choose Category</option>
                        {categories.map(({ id, name }) => (
                        <option value={id}>{name}</option>
                        ))}
                    </select>
                    <div className="form-group">
                        <label htmlFor="exampleFormControlTextarea1">Detail Product</label>
                        <textarea
                            className="form-control"
                            rows={3}
                            name="detail"
                            ref={register({
                                required: true,
                            })}
                        />
                        {errors.detail && errors.detail.type === "required" && <span className="text-danger">Bạn không để trống</span>}
                    </div>
                    <br/>

                        <button type="submit" className="btn btn-primary">Submit</button>
                    </form>
                </div>
            </div>
        </div>
   
   )
}

AddProducts.propTypes = {

}

export default AddProducts
