import React, { useState } from "react";
import PropTypes from "prop-types";
import { useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
const AddProducts = ({ onAdd }) => {
  const [valueInput, setValueInput] = useState({});
  const onHandleChange = (e) => {
    const { name, value } = e.target;
    setValueInput({
      ...valueInput,
      [name]: value,
    });
  };
  const history = useHistory();
  const onHandleSubmit = () => {
    console.log(valueInput);
    onAdd(valueInput);
    history.push("/admin/products");
  };
  const { register, handleSubmit, errors } = useForm();
  const onSubmit = (data) => console.log(data);

  return (
    <div>
      <h1 className="h3 mb-2 text-gray-800">Add Product Form</h1>
      <div className="card shadow mb-4">
        <div className="card-body">
          <form action="" onSubmit={handleSubmit(onHandleSubmit)}>
            <div className="form-group">
              <label htmlFor="">Tên sản phẩm</label>
              <input
                type="text"
                ref={register({
                  required: true,
                  minLength: 2,
                  maxLength: 5,
                })}
                name="name"
                className="form-control"
                onChange={onHandleChange}
              />
              {errors.name && errors.name.type === "required" && (
                <span className="text-danger">Bạn ko để trống </span>
              )}
              {errors.name && errors.name.type === "minLength" && (
                <span className="text-danger">Không được để khoảng trăng </span>
              )}
              {errors.name && errors.name.type === "maxLength" && (
                <span className="text-danger">Tối đa 5 kí tự</span>
              )}
            </div>

            <div className="form-group">
              <label htmlFor="">Ảnh</label>
              <input
                type="text"
                ref={register({
                  required: true,
                })}
                name="image"
                className="form-control"
                onChange={onHandleChange}
              />
              {errors.image && errors.image.type === "required" && (
                <span className="text-danger">Bạn ko để trống </span>
              )}
            </div>

            <div className="form-group">
              <label htmlFor="">Giá</label>

              <input
                type="text"
                ref={register({
                  required: true,
                })}
                name="price"
                className="form-control"
                onChange={onHandleChange}
              />
              {errors.price && errors.price.type === "required" && (
                <span className="text-danger">Bạn ko để trống </span>
              )}
            </div>
            <label htmlFor="" className="col-sm-2 col-form-label">
              Danh mục
            </label>
            <select className="form-control ml-4" name="id_cate" ref={register}>
              <option value={0}>Choose Category</option>
              {category.map(({ id, nameCate }) => (
                <option value={id}>{nameCate}</option>
              ))}
            </select>
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

AddProducts.propTypes = {};

export default AddProducts;
